    return (
      <Box alignitems="flex-start">
        <Box alignitems="flex-start" margin="0 0 20px 0">
          <Box justifycontent="flex-start" flexdirection="row">
            <Text>Sort By: </Text>
            <select>
              <option>Room</option>
              <option>Self</option>
            </select>
          </Box>
          <Box flexdirection="row" justifycontent="flex-start">
            {' '}<Text>Sort By: </Text>
            <select>
              <option>words</option>
              <option>uttered</option>
            </select>
            <select>
              <option>Asc</option>
              <option>Desc</option>
            </select>
          </Box>
        </Box>
        <Box alignitems="flex-start">
          {' '}<UtteredList utteredList={this.props.reducer.utteredList} />
        </Box>
      </Box>
    )
  }
}

const mapStateToProps = state => {
  return {
    reducer: state.utteredReducer
  }
}

const mapDispatchToProps = dispatch => {
  return {
    action: bindActionCreators({loadUtteredList, updateUtteredList}, dispatch)
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(UtteredContainer)
