import {bindActionCreators} from 'redux'
import {connect} from 'react-redux'
import styled from 'styled-components'
import ReactTable from 'react-table'
import validator from 'validator'
import 'react-table/react-table.css'
import data from './data'
import {reduxForm, Field} from 'redux-form'

import {Box, Flex, Form, Grid, Input, Label} from '../../components'

// actions
import {toggleFooter} from '../../actions/toggleFooterAction.js'

const validate = values => {
  const errors = {}
  if (!values.firstName) {
    errors.firstName = 'Required'
  }
  if (!values.lastName) {
    errors.lastName = 'Required'
  }
  if (!values.email) {
    errors.email = 'Required'
  } else if (!validator.isEmail(values.email)) {
    values.email = values.email || ''
    errors.email = 'Email not Valid'
  }

  return errors
}

class CreateCourse extends Component {
  constructor() {
    super()
    this.state = {
      search: '',
      selectedOption: ''
    }
    this.handleSearch = this.handleSearch.bind(this)
    this.handleIt = this.handleIt.bind(this)
    this.RenderInput = this.RenderInput.bind(this)
  }

  componentDidMount() {
    this.props.actions.toggleFooter(false)
  }

  componentWillUnmount() {
    this.props.actions.toggleFooter(true)
  }

  handleSearch(e) {
    this.setState({
      search: e.target.value
    })
  }

  handleOptionChange = selectedOption => {
    this.setState({selectedOption})
    console.log(`Selected: ${selectedOption.label}`)
  }

  handleIt(data) {
    // some ajax request here
    console.log('send data to server')
    console.log('data: ', data)
  }

  RenderInput({meta, input, label}) {
    return (
      <Box
        className={[
          meta.error && meta.touched ? 'courseError' : '',
          meta.active ? 'courseActive' : ''
        ].join(' ')}>
        <Label>{label}</Label>
        {/* <pre>{JSON.stringify(meta, 0, 2)}</pre>*/}
        <Input autoComplete="course" {...input} placeholder={label} />
        {meta.error && meta.touched && <span>{meta.error}</span>}
      </Box>
    )
  }

  render() {
    const {selectedOption} = this.state
    const value = selectedOption && selectedOption.value
    const {handleSubmit} = this.props

    return (
      <Grid>
        <Flex>
          <Form onSubmit={handleSubmit(this.handleIt)}>
            <Box>
              <Field
                autoComplete="given-name"
                name="firstName"
                label="First Name"
                component={this.RenderInput}
              />
            </Box>
            <Box>
              <Field
                autoComplete="family-name"
                name="lastName"
                label="Last Name"
                component={this.RenderInput}
              />
            </Box>
            <Box>
              <Field
                name="email"
                autoComplete="email"
                label="email"
                component={this.RenderInput}
              />
            </Box>
          </Form>
        </Flex>
      </Grid>
    )
  }
}

const mapStateToProps = state => {
  return {}
}

const mapDispatchToProps = dispatch => {
  return {
    actions: bindActionCreators(
      {
        toggleFooter
      },
      dispatch
    )
  }
}

CreateCourse = connect(mapStateToProps, mapDispatchToProps)(CreateCourse)

export default reduxForm({
  form: 'CreateCourse',
  validate
})(CreateCourse)
