  grid-template-areas:
    'sidebar sidebar'
    'content content';
  min-height: 600px;
  height: 1200px;

  @media (min-width: 640px) {
    grid-template-areas: 'sidebar content';
  }
`

const StyledNavLink = styled(NavLink)`
  padding: 20px;
  font-size: 1.5rem;
  &:visited {
    color: blue;
  }
  &:hover {
    color: green;
  }
`

class CoursesContainer extends Component {
  constructor() {
    super()
    this.state = {
      search: '',
      selectedOption: ''
    }
    this.handleSearch = this.handleSearch.bind(this)
    this.handleOptionChange = this.handleOptionChange.bind(this)
  }

  handleSearch(e) {
    this.setState({
      search: e.target.value
    })
  }

  handleOptionChange = selectedOption => {
    this.setState({selectedOption})
    console.log(`Selected: ${selectedOption.label}`)
  }

  render() {
    const {selectedOption} = this.state
    const value = selectedOption && selectedOption.value

    return (
      <StyledGrid>
        <Staticbar>
          <Flex gridarea="sidebar">
            <Subtitle>I speak:</Subtitle>
            <Box>
              <Select
                name="form-field-name"
                value={value}
                onChange={this.handleChange}
                options={[
                  {value: 'one', label: 'One'},
                  {value: 'two', label: 'Two'}
                ]}
              />
            </Box>
            <Box>
              <StyledNavLink to="/my-courses">My Created Courses</StyledNavLink>
            </Box>
          </Flex>
        </Staticbar>
        <Grid gridarea="content">
          <Flex>
            <Title>Enroll in a Course</Title>
            <Subtitle>Choose from several languages</Subtitle>
          </Flex>
        </Grid>
      </StyledGrid>
    )
  }
}

