const Room = ({
  level,
  isSelected,
  joinRoom,
  updateCurrentRoom,
  creator,
  people,
  title
}) => {
  if (title !== 'Lobby') {
    var room = (
      <StyledRoom selected={isSelected}>
        <Text fontsize="20px" onClick={joinRoom}>
          {title} <span style={{fontSize: '15px'}}>( {people} )</span>
        </Text>
        <Text>level:{level}</Text>
        <Text>creator:{creator}</Text>
