      //         return (
      //           <Room joinRoom={() => joinRoom(`${title}`)} title={title} />
      //         )
      //       }
      //     })}
      //   </div>
      // )
    }
    return (
      <div>
        {renderLobby}
        {renderList}
      </div>
    )
  }
}

